  max-width: 100%;
  height: 100%;
  position: relative;
  flex-grow: 1;
}

#markdown-editor {
  display: flex;
  width: 500px;
  height: 450px;
  border: 1px solid black;
}

.draft-name-input {
  height: 55px;
  padding: 10px;
  font-size: 30px;
  margin-bottom: 20px;
  border: 2px solid black;
  border-radius: 4px;
  align-items: center;
  justify-content: center;
}

.markdown-preview {
  margin-top: 10px;
  margin-bottom: 10px;
  overflow-y: auto;
  height: 200px;
  width: 500px;
  border: 1px solid black;
}

.save-button {
  align-self: center;
}
</style>
