<template>
    <input class="draft-name-input" v-model="draftName" placeholder="Enter draft name" />
    <!--<div v-html="compiledMarkdown " class="markdown-preview"></div>-->
    <quill-editor ref="quill" id="quill-editor" editorContent="quill" @contentChange="updateMarkdown"></quill-editor>
    <savebuttoncontainer class="save-button" @click="save"></savebuttoncontainer>
</template>

<script>
/*
import savebuttoncontainer from '. ./components/savebuttoncontainer'
import { ref, computed, watch } from 'vue';
import MarkdownIt from 'markdown-it';
import { jsPDF } from 'jspdf';
import axios from 'axios';
*/
//import 'quill/dist/quill.snow.css'; // for snow theme
import { quillEditor } from 'vue3-quill';
import savebuttoncontainer from '../components/savebuttoncontainer';
import { ref, onMounted, watch } from 'vue';
import { jsPDF } from 'jspdf';
import axios from 'axios';

export default {
    name: 'Drafts',
    components: {
        savebuttoncontainer,
        quillEditor
    },
    props: {
        project_id: {
            type: String,
            required: true,
        },
    },
    setup(props) {

        const draftName = ref('');
        const editorContent = ref('');
        //const md = new MarkdownIt();
        const project_id = ref(props.project_id);
        const quill = ref(null);

        onMounted(() => {
            quill.value = quillEditor.editor; // Access the Quill editor instance
        });

        const updateMarkdown = ({ quill }) => {
            editorContent.value = quill.root.innerHTML;
            console.log('Content changed:', editorContent.value);
        };

        const save = async () => {
            let content = '';
            if (quill.value) {
                content = JSON.stringify(quill.value.getContents());
            } 
            console.log('Quill editor content:', content);
            const doc = new jsPDF();
            await doc.html(content, { // Wait for the HTML to be rendered
                x: 10,
                y: 10
            });

            const pdf = doc.output('blob');
            console.log('Draft name:', draftName.value);


            try {
                const formData = new FormData();
                formData.append('action', 'create_draft');
                formData.append('file', pdf, `${draftName.value}.pdf`);
                formData.append('file_data', JSON.stringify({
                    content: content,
                    filename: `${draftName.value}.pdf`
                }));

                const response = await axios.post(`${process.env.VUE_APP_FLASK_APP_URL}/project_workspace/${props.project_id}`, formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });

                if (response.status === 200) {
                    console.log('Draft saved successfully');
                } else {
                    console.log('Failed to save draft');
                }
            } catch (error) {
                console.error(error);
            }
        };
        return {
            draftName,
            quill,
            project_id,
            save,
            updateMarkdown
        };
    },
};
</script>

