import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    creatorId: null
  },
  mutations: {
    setCreatorId(state, creatorId) {
      state.creatorId = creatorId;
    }
  },
  actions: {
    login({ commit }, { creatorId }) {
      commit('setCreatorId', { creatorId });
    },
  },
  getters: {
    currentCreatorId(state) {
      return state.creatorId;
    }
  }
});
