<template>
    <input class="draft-name-input" v-model="draftName" placeholder="Enter draft name" />
    <!--<div v-html="compiledMarkdown " class="markdown-preview"></div>-->
    <quill-editor @ready="onEditorReady" ref="quill" id="quill-editor" editorContent="quill" ></quill-editor>
    <savebuttoncontainer class="save-button" @click="save"></savebuttoncontainer>
</template>

<script>
import { ref } from 'vue';
import { quillEditor } from 'vue3-quill';
import savebuttoncontainer from '../components/savebuttoncontainer';
import { jsPDF } from 'jspdf';
import axios from 'axios';
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

export default {
    name: 'Drafts',
    components: {
        savebuttoncontainer,
        quillEditor
    },
    props: {
        project_id: {
        type: String,
        required: true,
        },
    },
    data() {
        return {
            draftName: '',
            quill: null,
            project_id: this.project_id,
            editorContent: ''
        };
    },
    methods: {
        onEditorReady(editor) {
            this.quill = editor;
        },
        async save() {
            if (this.quill) {
                const delta = this.quill.getContents();

                const content = delta.ops.map(op => {
                    if (op.insert) {
                        if (typeof op.insert === 'string') {
                            return { text: op.insert, ...op.attributes };
                        } else if (op.insert.image) {
                            return { image: op.insert.image };
                        }
                    }
                    return {};
                });

                const docDefinition = { content };

                try {
                    const pdfDocGenerator = pdfMake.createPdf(docDefinition);
                    const pdfDataUrl = await new Promise((resolve, reject) => {
                        pdfDocGenerator.getDataUrl((dataUrl) => {
                            resolve(dataUrl);
                        });
                    });

                    const response = await axios.post(`${process.env.VUE_APP_FLASK_APP_URL}/project_workspace/${this.project_id}`, {
                        action: 'create_draft',
                        file: pdfDataUrl,
                        filename: `${this.draftName}.pdf`,
                    });

                    if (response.status === 200) {
                        console.log('Draft saved successfully');
                    } else {
                        console.log('Failed to save draft');
                    }
                } catch (error) {
                    console.error('An error occurred while saving the draft:', error);
                }
            }
        }
    }
};
</script>

